"import 'dart:convert';\nimport 'package:http/http.dart' as http;\n\nclass MacVendorApi {\n  final String _baseUrl = 'https://api.macvendors.com/';\n\n  Future<String?> getVendor(String macAddress) async {\n    try {\n      final response = await http.get(Uri.parse('\$_baseUrl\$macAddress'));\n      if (response.statusCode == 200) {\n        return response.body;\n      }\n      return null;\n    } catch (e) {\n      print(\"Erro ao buscar fabricante: \$e\");\n      return null;\n    }\n  }\n}"